<script>
    import {
        mapGetters
    } from "vuex";
    export default {
        layout: 'checkout',
        data() {
            return {
                paymentType: 'Card',
            }
        },
        computed: {
            ...mapGetters({
                cartValue: 'cart/getCartValue',
            }),
            cardFee() {
                return (parseInt(this.cartValue) / 100) * 3.75;
            },
            total() {
                if (this.paymentType == 'Card') {
                    return this.cartValue + this.cardFee + 200;
                } else return this.cartValue + 200
            }
        },
        methods: {
            placeOrder() {
                let payload = {
                    amount: this.total,
                    payment_type: this.paymentType,
                };
                this.$repositories.cart.placeOrder(payload).then(res => {
                    console.log(res);
                    this.$store.dispatch('cart/resetCart').then(() => {
                        this.$router.replace('/checkout/thank-you');

                    });
                }).catch(err => {
                    console.log(err);
                })
            }
        }
    }

</script>

